import React, { useReducer, } from 'react'
import './SignUp.css'
const nameValid = RegExp(/[0-9]/)
const validEmailRegex = RegExp(
	/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i,
)

const initialState = {
	nameValue: {
		name: '',
		isValid: false,
		error: ''
	},
	emailValue: {
		email: '',
		isValid: false,
	},
	passwordValue: {
		password: '',
		isValid: false,
	},
}
const UserReducer = (state, action) => {
	switch (action.type) {
		case 'NAME':
			return {
				...state,
				nameValue: {
					name: action.value,
					isValid: nameValid.test(action.value),
				},
			}
		case 'EMAIL':
			return {
				...state,
				emailValue: {
					email: action.value,
					isValid: validEmailRegex.test(action.value),
				},
			}
		case 'PASSWORD':
			let a = action.value.split('', 2).join()
			let b = action.value.split('').reverse().join('')
			return {
				...state,
				passwordValue: {
					password: b + a,
					isValid: action.value.length > 5,
				},
			}
		case 'NAME_BLUR':
			return {
				...state,
				nameValue: {
					name: action.value,
					isValid: nameValid.test(action.value),
					error: nameValid.test(state.value)? '' :state.value ===""? 'введите имя' : '' 
				},
			}
		// case 'EMAIL_BLUR':
		// 	return {
		// 		...state,
		// 		emailValue: {
		// 			email: action.value,
		// 			isValid: validEmailRegex.test(action.value),
		// 		},
		// 	}
		// case 'PASSWORD_BLUR':
			
		// 	return {
		// 		...state,
		// 		passwordValue: {
		// 			password: action.value,
		// 			isValid: action.value.length > 5,
		// 		},
		// 	}

		default:
			break
	}
}
function SignUp(props) {
	const [user, dicpatchUser] = useReducer(UserReducer, initialState)
	const NameChangeHandler = (event) => {
		dicpatchUser({ type: 'NAME', value: event.target.value })
	}
	const EmailChangeHandler = (event) => {
		dicpatchUser({ type: 'EMAIL', value: event.target.value })
	}
	const PasswordChangeHandler = (event) => {
		dicpatchUser({ type: 'PASSWORD', value: event.target.value })
	}
	const NameBlurHandler = (event)=>{
		dicpatchUser({type:'NAME_BLUR' })
	}
	const SubmitHandler = (event) => {
		event.preventDefault()
		// const newData = {
		// 	name: name,
		// 	email: email,
		// 	password: password,
		// 	id: Math.random().toString(),
		// }
		// console.log(newData)
		console.log(user)
	}

	return (
		<form onSubmit={SubmitHandler} className='input_form'>
			<label>username</label>
			<input
				onChange={NameChangeHandler}
				onBlur={NameBlurHandler}
				type='text'
				placeholder='username'
			/>

			<label>login</label>
			<input
				onChange={EmailChangeHandler}
				type='email'
				placeholder='login'
			/>

			<label>password</label>
			<input
				onChange={PasswordChangeHandler}
				type='password'
				placeholder='password'
			/>

			<button>OK</button>
		</form>
	)
}

export default SignUp
