import React, { useEffect, useReducer, useState } from 'react'
import './SignUp.css'
const nameValidRegExp = RegExp(/['0-9']/)
const validEmailRegex = RegExp(
	/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i,
)

const initialState = {
	nameValue: {
		value: '',
		isValid: null,
		error: '',
	},
	emailValue: {
		value: '',
		isValid: null,
		error: '',
	},
	passwordValue: {
		value: '',
		isValid: null,
		error: '',
	},
}
const UserReducer = (state, action) => {
	switch (action.type) {
		case 'NAME':
			return {
				...state,
				nameValue: {
					value: action.value,
					isValid: nameValidRegExp.test(action.value),
				},
			}
		case 'EMAIL':
			return {
				...state,
				emailValue: {
					value: action.value,
					isValid: validEmailRegex.test(action.value),
				},
			}
		case 'PASSWORD':
			let a = action.value.split('', 2).join()
			let b = action.value.split('').reverse().join('')
			return {
				...state,
				passwordValue: {
					value: b + a,
					isValid: action.value.length > 5,
				},
			}
		case 'NAME_BLUR':
			return {
				...state,
				nameValue: {
					value: state.nameValue.value,
					isValid: nameValidRegExp.test(state.nameValue.value),
					error: nameValidRegExp.test(state.nameValue.value)
						? ''
						: state.nameValue.value === ''
						? 'введите имя'
						: 'username must have digits',
				},
			}
		case 'EMAIL_BLUR':
			return {
				...state,
				emailValue: {
					value: state.emailValue.value,
					isValid: validEmailRegex.test(state.emailValue.value),
					error: validEmailRegex.test(state.emailValue.value)
						? ''
						: state.emailValue.value === ''
						? 'введите email'
						: 'gmail is not valid',
				},
			}
		case 'PASSWORD_BLUR':
			return {
				...state,
				passwordValue: {
					password: state.passwordValue.value,
					isValid: state.passwordValue.value.trim().length > 5,
					error:
						state.passwordValue.value.trim().length > 5
							? ''
							: state.passwordValue.value === ''
							? 'true'
							: 'not correct',
				},
			}

		default:
			break
	}
}
function SignUp(props) {
	const [disabled, setDisabled] = useState(false)
	useEffect(() => {
		const identifer = setTimeout(() => {
			setDisabled()
		}, 500)

		return () => {
			clearTimeout(identifer)
		}
	}, [])
	const [user, dicpatchUser] = useReducer(UserReducer, initialState)
	const NameChangeHandler = (event) => {
		dicpatchUser({ type: 'NAME', value: event.target.value })
	}
	const EmailChangeHandler = (event) => {
		dicpatchUser({ type: 'EMAIL', value: event.target.value })
	}
	const PasswordChangeHandler = (event) => {
		dicpatchUser({ type: 'PASSWORD', value: event.target.value })
	}
	const NameBlurHandler = (event) => {
		dicpatchUser({ type: 'NAME_BLUR' })
	}
	const EmailBlurHandler = () => {
		dicpatchUser({ type: 'EMAIL_BLUR' })
	}
	const PasswordBlurHandler = () => {
		dicpatchUser({ type: 'PASSWORD_BLUR' })
	}
	console.log(user)
	const SubmitHandler = (event) => {
		event.preventDefault()
		console.log(user)
	}

	return (
		<form onSubmit={SubmitHandler} className='input_form'>
			<label>username</label>
			<input
				onChange={NameChangeHandler}
				onBlur={NameBlurHandler}
				type='text'
				placeholder='username'
			/>
			<p>{user.nameValue.error}</p>

			<label>login</label>
			<input
				onChange={EmailChangeHandler}
				type='email'
				onBlur={EmailBlurHandler}
				placeholder='login'
			/>
			<p>{user.emailValue.error}</p>

			<label>password</label>
			<input
				onChange={PasswordChangeHandler}
				type='password'
				placeholder='password'
				onBlur={PasswordBlurHandler}
			/>
			<p>{user.passwordValue.error}</p>
			<button disabled={!di}>OK</button>
		</form>
	)
}

export default SignUp
